-- Function structure for floor_times
-- ----------------------------
DROP FUNCTION IF EXISTS `floor_times`;
delimiter ;;
CREATE FUNCTION `floor_times`(`i_left` DECIMAL(30,10))
 RETURNS decimal(30,10)
BEGIN
    RETURN floor(i_left * 10000)/10000;
END
;;
delimiter ;

