CREATE FUNCTION `funCashFlowType`(i_remark  varchar(128))
 RETURNS int(11)
label_pro: BEGIN
    declare v_type int(11) default 0;  
    
    set v_type = case when i_remark like 'CorpAction-%' then 10                     -- 
                      when i_remark like 'CorpAction %' then 11                     -- 
                      when i_remark like 'IPO Apply Handling Fee%' then 5           -- IPO  5 
                      when i_remark like '%Placing Order Handling Fee%' then 5
                      when i_remark like 'IPO Apply%' then 4                        -- IPO , IPO 
                      when i_remark like 'IPO Refund%' then 8                       -- IPO 
                      when i_remark like 'IPO Loan Return%' then 7                  -- IPO 
                      when i_remark like '%Placing Order%' then 7 
                      when i_remark like 'IPO Loan Interest%' then 9                -- IPO 
                      when i_remark like 'IPO Loan%' then 6                         -- IPO 
                      when i_remark like 'conversion;%' then 3                      -- ( 2 )
                      when i_remark like '%%' then 15                        --  
                      when i_remark like '%%' then 16                        -- 
                      when i_remark like 'Portfolio Fee%' then 14                   -- 
                      when i_remark like 'Monthly Interest Received%' then 12       -- 
                      when i_remark like 'Monthly Interest Paid%' then 13           -- 
                      when i_remark like ';%' then 1                            -- 
                      when i_remark like ';%' then 2                            -- 
                      else 0                                                        -- 
                      end;
    
    return v_type; 
END
;;
delimiter ;

