DROP PROCEDURE IF EXISTS `proBackup`;
delimiter ;;
CREATE PROCEDURE `proBackup`(OUT o_ret_code int, 
  OUT o_ret_msg varchar(64))
label:begin
    
    declare v_buss_date date default NULL;  
    declare v_last_buss_date date default NULL;

    set o_ret_code = 0, o_ret_msg = "Success";
    start transaction ; 

    select last_buss_date,buss_date into v_last_buss_date,v_buss_date from sys_info; 
    
    if v_buss_date is NULL then 
        set o_ret_code = 1, o_ret_msg = "Bussness date not setting up"; 
        rollback; 
        leave label; 
    end if;

    INSERT INTO currency_history
    (
        process_date,
        currency,
        currency_desc, 
        prime_rate, 
        saving_rate, 
        exchange_rate,
        debit_formula_type, 
        debit_formula_code,
        credit_formula_type, 
        credit_formula_code, 
        base, 
        update_user, 
        update_time
    )
    select 
        v_last_buss_date,
        currency,
        currency_desc, 
        prime_rate, 
        saving_rate, 
        exchange_rate,
        debit_formula_type, 
        debit_formula_code,
        credit_formula_type, 
        credit_formula_code, 
        base, 
        update_user,
        now()
    from currency;

    INSERT INTO currency_ratio_history
    (
        process_date,
        currency_from,
        currency_to,
