    SET o_ret_code = 1, o_ret_msg = "database table is empty";
    LEAVE label;
END IF;

START TRANSACTION;

OPEN cur_req;
loop_cycle: LOOP
    SET v_account_id = NULL;
    FETCH cur_req INTO v_account_id,v_bcan,v_rslt_bcan,v_fimg_bcan,v_fimg_rs;
    IF v_done <> 0 THEN
