CLOSE cur_req;

UPDATE bcan_mapp_info bmi INNER JOIN bcan_rslt_fimg brf ON bmi.account_id=brf.account_id AND bmi.status<>brf.fimg_rs SET status=brf.fimg_rs,update_time=NOW();

