SET o_ret_code = 0, o_ret_msg = "SUCCESS";

-- (20210210)C++tmp_bcan_rslt_fimg,,SQL;
CREATE INDEX idx_1 ON tmp_bcan_rslt_fimg(rslt_bcan);
CREATE INDEX idx_2 ON tmp_bcan_rslt_fimg(fimg_sfirm);
