IF EXISTS(SELECT 1 FROM tmp_bcan_rslt_fimg t LEFT JOIN bcan_mapp_info b ON t.rslt_bcan=b.bcan WHERE t.fimg_rs <> 'S' and b.account_id IS NULL)THEN
