            a.debit_interest_rate = ifnull(b.debit_interest_rate,0),
            a.credit_interest_rate = ifnull(b.credit_interest_rate,0)
        where a.process_date = v_process_date;

    set o_ret_code = 0; 
    set o_ret_msg = "";

end
