            contract_date,
            input_channel,
            remark,
            exec_time,
            exec_no,
            exchange_ref_no,
            exec_broker,
            contra_broker,
            exec_quantity,
            exec_price,
            code,
            mark,
            num_of_trades,
            trade_amount,
            net_amount
        )
        select 
            trade_id,
            max(trade_exec_id),
            max(process_date),
            max(order_no),
            max(order_date),
            max(order_time),
            max(business_department),
            max(department_trading_group),
            max(account_id),
            max(account_type),
            max(trade_date),
            max(settle_date),
            max(market_id),
            max(list_market_id),
            max(product_type),
            max(product_sub_type),
            max(product_id),
            max(product_description),
            max(quote_currency),
            max(clearing_currency),
