DROP TABLE IF EXISTS tmp_rpt1;
CREATE TEMPORARY TABLE tmp_rpt1
(
    process_date DATE,
    account_id VARCHAR(32),
    short_name VARCHAR(128),
    ae_code VARCHAR(16),
    buy_quantity INT,
    sell_quantity INT,
    total_quantity INT,
    currency VARCHAR(16),
