DROP PROCEDURE IF EXISTS `proExportRollingBalanceCashClientAnalysis`;
delimiter ;;
CREATE PROCEDURE `proExportRollingBalanceCashClientAnalysis`(in i_process_date date)
label:begin

    


    if i_process_date  is null then 
