        where id = v_id; 

        update account_position a 
        set a.nominee_quantity = funMin(a.quantity, a.nominee_quantity)
        where a.id = v_id; 
    end if; 

    
    select quantity into v_balance from account_position 
        where process_date = v_process_date and 
          market_id = i_market_id and 
