    OUT o_ret_code int,
    OUT o_ret_msg char(128))
label:begin
    DECLARE v_calendar_day date;
    DECLARE v_process_date date;
    DECLARE v_t2 date;
    declare v_account_id varchar(16) default NULL;
    declare v_product_id varchar(32) default '';
    declare v_market_id int default 0;   
